mapper.update:
update book
title = #{title}, author = #{author}, publisher = #{publisher}, isbn = #{isbn}, publishDate = #{publishDate}, category = #{category}, cover = #{cover}, description = #{description}, location = #{location}, callNumber = #{callNumber}, availableCopies = #{availableCopies}, totalCopies = #{totalCopies}, available = #{available},
where id = #{id}
